import { React, useState } from "react"
import { useParams } from "react-router-dom"
import { deleteCard } from "../../utils/api"
import NavBar from "../NavBar"
import EditCard from "./EditCard"


function ViewDeck(){
    const { deckId } = useParams()
    const [ currentDeck, setCurrentDeck ] = useState()
    const [ currentCards, setCurrentCards ] = useState()

    const handleDeletedCard = (id) => {
        if (window.confirm("Delete this card? You will not be able to recover it.")) {
            deleteCard(id)
            window.location.reload()
         }
      }

    useEffect(() => {
        async function loadDeck() {
            try {
                const response = await readDeck(deckId)
                setCurrentDeck(response)
                setCurrentCards(response.cards)
            } catch (error) {
                if (error.name === "AbortError") {
                console.log("Aborted")
                } else {
                throw error
                }
            }
        }
        loadDeck()
    }, [deckId])

    return(
        <div>
            <NavBar subLink={`/decks/${deckId}`} subLinkName={`Deck ${currentDeck.name}`} currentPage="View Deck" />
            <h2>{currentDeck.name}</h2>
            <p>{currentDeck.description}</p>
            <div className='d-flex'>
            <Link to={`${url}/edit`}>Edit</Link>
            <Link to={`${url}/study`}>Study</Link>
            <Link to={`${url}/cards/new`}>Add Card</Link>
            <button onClick={handleDeleteDeck}>Delete</button>
        </div>
            {currentCards.map((eachCard, index) => (
                <Card card={eachCard} key={index} handleCardDelete={() => handleDeletedCard(eachCard)}
                />
            ))
            }

        </div>
    )
}

export default ViewDeck
